<script>
  import frequency from '../content/frequency'
  import {mapState} from "vuex";

  export default {
    data() {
      return {
        frequency,
        activeKey: null,
      }
    },

    computed: {
      ...mapState('userPreference', ['isListenMode', 'tuning']),
    },

    methods: {
      onClick(key) {
        if (this.isListenMode) {
          const note = this.tuning[key].toLowerCase()
          const audio = new Audio(`/_nuxt/assets/sounds/${note}.mp3`);
          audio.play()
        }

        this.activeKey = key
        this.$emit('click', key)
      }
    },
  }
</script>
